<?phpinclude_once "config.inc.php";include_once "ipadRegistration.class.php";//DATABASE CONFIGURATIONS$host = $CONFIG['DATABASE'][0]['HOST'];$user = $CONFIG['DATABASE'][0]['USERNAME'];$password = $CONFIG['DATABASE'][0]['PASSWORD'];$db = $CONFIG['DATABASE'][0]['DATABASE'];//------>print "iPad getCodeReg - ".date("Y-m-d H:i:s")."\n";print "----------------------------------------------\n";print "attempt to connect database : ";$conn = mysql_connect($host,$user,$password);if($conn){	print "OK\n";}else{	print "FAILED\n";}if($conn){	print "Check table ===== ";	$qry = "SELECT * FROM ".$db.".ipad_code_registration ORDER BY id DESC LIMIT 1;";	$q = mysql_query($qry,$conn);	if($q){		print "OK\n";		$res = mysql_fetch_array($q);		$lastid = $res['id']; 		$curr_lastid = $lastid;	}else{		print "FAILED\n".mysql_error()."\n";				print "Create table ipad_code_registration ===== ";				$qry = "CREATE TABLE ".$db.".ipad_code_registration(				  `id` INT(11) NOT NULL,				  `codeid` VARCHAR(10) NOT NULL,				  `codetype` INT(3) NOT NULL,				  `survey_date` DATETIME NOT NULL,				  `surveyor` VARCHAR(35) NOT NULL,				  `email`	VARCHAR(75) NOT NULL,				  PRIMARY KEY  (`id`)				)";		$q = mysql_query($qry,$conn);		if($q){print "OK\n";}else{print "FAILED\n".mysql_error()."\n";}				$lastid = 0;			}		print "Fetching data ===== \n";		$ipad = new ipadRegistration();	$n=360;	$retry = 0;	while($n>0){		if($retry==10){			print "no more data... quiting\n";			break;		}		print "get from api with last id => ".$lastid;				$response  = json_decode($ipad->getCodeReg($lastid));		$lastid = $response->lastid;				print " || get last id => ".$lastid."\n";				if($lastid>0){			$data = $response->codedata;			foreach($data as $k => $v){				$qry = "INSERT IGNORE INTO ".$db.".ipad_code_registration						(id,codeid,codetype,survey_date,surveyor,email)						VALUES ('".$v->id."','".$v->codeid."','".$v->codetype."','".$v->survey_date."','".$v->surveyor."','".$v->email."')";				$q = mysql_query($qry,$conn);				if($q){					//print "OK\n";				}else{					//print "FAILED\n";					//print mysql_error()."\n=====================\n";				}							}			$curr_lastid = $lastid;		}else{			$lastid = $curr_lastid;			sleep(1);			$retry++;		}		$n--;	}		print "Closing Database Connection ";		mysql_close($conn);	print "OK\n";}print "DONE\n---------------------------\n";?>